namespace HanafudaAdvisor.Wpf.Models { public enum CardType{Hikari,Tane,Tanzaku,Kasu} public sealed record Card(int Month, CardType Type, string? Name=null, bool IsRain=false, bool IsRedPoem=false, bool IsBlueRibbon=false, bool IsBoar=false, bool IsDeer=false, bool IsButterfly=false, bool IsCup=false){ public override string ToString()=> $"{Month:D2}:{Type}{(Name is null ? "" : ":" + Name)}"; } }