using System.Collections.Generic;
using System.Linq;
namespace HanafudaAdvisor.Wpf.Models { public static class Deck { public static IReadOnlyList<Card> Full { get; } = Build(); public static System.Collections.Generic.IEnumerable<int> Months => Enumerable.Range(1, 12); public static System.Collections.Generic.IEnumerable<CardType> Types => new[] { CardType.Hikari, CardType.Tane, CardType.Tanzaku, CardType.Kasu }; private static System.Collections.Generic.List<Card> Build() { var list = new System.Collections.Generic.List<Card>(48); for (int m = 1; m <= 12; m++) { list.Add(new Card(m, CardType.Tanzaku, $"{m}譛・遏ｭ蜀・)); list.Add(new Card(m,CardType.Tane,$"{ m }譛・繧ｿ繝・)); list.Add(new Card(m, CardType.Kasu, $"{m}譛・繧ｫ繧ｹ1")); list.Add(new Card(m, CardType.Kasu, $"{m}譛・繧ｫ繧ｹ2")); } Replace(1, CardType.Hikari, name:"譚ｾ縺ｫ鮓ｴ", redPoem:true); Replace(2, CardType.Tanzaku, name:"譴・↓遏ｭ蜀・,redPoem:true); Replace(3,CardType.Hikari,name:"譯懊↓蟷・, redPoem:true); Replace(6, CardType.Tanzaku, name:"迚｡荳ｹ縺ｫ髱堤洒", blueRibbon:true); Replace(6, CardType.Tane, name:"陜ｶ", isButterfly:true); Replace(7, CardType.Tane, name:"迪ｪ", isBoar:true); Replace(8, CardType.Hikari, name:"闃偵↓譛・); Replace(9,CardType.Tane,name:"闖翫↓逶・, isCup:true); Replace(9, CardType.Tanzaku, name:"闖翫↓髱堤洒", blueRibbon:true); Replace(10, CardType.Tane, name:"鮖ｿ", isDeer:true); Replace(10, CardType.Tanzaku, name:"邏・痩縺ｫ髱堤洒", blueRibbon:true); Replace(11, CardType.Hikari, name:"譟ｳ縺ｫ蟆城㍽驕馴｢ｨ", isRain:true); Replace(12, CardType.Hikari, name:"譯舌↓魑ｳ蜃ｰ"); return list; void Replace(int month, CardType type, string? name = null, bool isRain = false, bool redPoem = false, bool blueRibbon = false, bool isBoar = false, bool isDeer = false, bool isButterfly = false, bool isCup = false) { var idx = list.FindIndex(c => c.Month == month && c.Type == type); if (idx >= 0) { var c = list[idx]; list[idx] = new Card(month, type, name ?? c.Name, isRain, redPoem, blueRibbon, isBoar, isDeer, isButterfly, isCup); } } } } }